           buildTypes {
               debug {
                   matchingFallbacks = ['release', 'debug']
                   proguardFiles getDefaultProguardFile('proguard-android.txt'), 'proguard-rules.pro'
                   signingConfig signingConfigs.release
                   minifyEnabled false
                   ext.enableCrashlytics = false
                   ext.alwaysUpdateBuildId = false

               }
               release {
                   matchingFallbacks = ['release', 'debug']
                   minifyEnabled true
